import { useEffect, useState, useContext } from 'react';
import './view.css';
import Spinner from '../../components/core/spinner/spinner.component';
import FilterBar from '../../components/view/filter-bar/filter-bar.component';
import Item from '../../components/view/item/item.component';
import { getCartQuantity } from '../../utils/cart';
import { CartContext } from '../../components/providers/cart-provider.component';
import { fetchItems } from '../../services/items.service';
import useFilterItems from '../../hooks/filter-items.hook';
import useToggle from '../../hooks/common/toggle.hook';

/**
 * @type {Array<{
 * id: number;
 * name: string;
 * description: string;
 * ingredients: string[];
 * price: number;
 * category: string;
 * image: string;
 * }>}
 */
const initialItems = [];

const ViewPage = () => {
  const [menuItems, setMenuItems] = useState(initialItems);
  const [loading, setLoading] = useState(false);
  const [isTourist, toggleIsTourist] = useToggle(false);

  const cartContext = useContext(CartContext);

  const getMenuItems = async () => {
    setLoading(true);
    const items = [
      {
      "id": "55101",
      "name": "fatoosh",
      "price": 20,
      "description": "best meal ever",
      "category": "Salads",
      "ingredients": [
      "tomato",
      "cucumber",
      "onion"
      ],
      "image": "https://upload.wikimedia.org/wikipedia/commons/9/93/Fattoush_mixed-salad.jpg"
      },
      {
      "id": "55111",
      "name": "makloba",
      "price": 50,
      "description": "makloba 3kef kefak",
      "category": "Main Dish",
      "ingredients": [
      "rice",
      "chicken"
      ],
      "image": "https://www.hungrypaprikas.com/wp-content/uploads/2021/04/Maqluba-New-1-1024x1536.jpg"
      },
      {
      "id": "55012",
      "name": "shawermah",
      "price": 15,
      "description": "shawerma al haram",
      "category": "Main Dish",
      "ingredients": [
      "chicken",
      "garlic",
      "fries"
      ],
      "image": "https://images.alarab.com/data/news/2016/12/02/781532/20161202170922alarab87598.jpg?auto_optimize=high?auto_optimize=high"
      },
      {
      "id": "5513",
      "name": "Knafeh",
      "image": "https://i.pinimg.com/originals/5c/52/e4/5c52e4cd9822a681bdd8f2e619c4094d.jpg",
      "description": "Is a sugar-soaked pastry that is common in a number of Arabic countries\n",
      "price": 15,
      "category": "Ice Cream",
      "ingredients": [
      "Cheese",
      "Pastry",
      "Syrup",
      "Pistachio "
      ]
      }];
    setMenuItems(items);
    setLoading(false);
  };

  useEffect(() => { getMenuItems(); }, []);

  const filteredItems = useFilterItems(menuItems, isTourist);  

  return (
    <div className="view-page">
      <h1>View Menu Items</h1>
      <FilterBar isTourist={isTourist} toggleIsTourist={toggleIsTourist} />
      {
        loading
          ? <div style={{ display: 'flex', justifyContent: 'center', marginTop: 50 }}><Spinner /></div>
          : (
            <div className="items-container">
              {
                filteredItems.length
                  ? filteredItems.map((item, index) => (
                    <Item
                      data={item}
                      key={item.name + index}
                      dispatch={cartContext.dispatch}
                      cartQuantity={getCartQuantity(item.id, cartContext.cart)}
                    />
                  ))
                  : (
                    <div className="no-results">
                      <img src="./frustrated-realistic.png" alt="No results" />
                      <p>No results found</p>
                    </div>
                  )
              }
            </div>
          )
      }
    </div>
  );
};

export default ViewPage;
